deployment.deployer:
variable "secgroup_id" {}
data "huaweicloud_availability_zones" "myaz" {}
data "huaweicloud_compute_flavors" "myflavor" {
    availability_zone = data.huaweicloud_availability_zones.myaz.names[0]
    performance_type  = "normal"
    cpu_core_count    = 2
    memory_size       = 4
}
data "huaweicloud_vpc_subnet" "mynet" {
    name = "subnet-20a3-wmm"
}
resource "huaweicloud_compute_instance" "basic" {
    name               = "basic-wmm"
    image_id           = "a8601887-81d5-4eed-9338-382cf5b6d80b"
    flavor_id          = data.huaweicloud_compute_flavors.myflavor.ids[0]
    availability_zone  = data.huaweicloud_availability_zones.myaz.names[0]
    network {
        uuid = data.huaweicloud_vpc_subnet.mynet.id
    }
}
resource "huaweicloud_evs_volume" "myvol" {
    name              = "volume"
    availability_zone = data.huaweicloud_availability_zones.myaz.names[0]
    volume_type       = "SAS"
    size              = 10
}
resource "huaweicloud_compute_volume_attach" "attached" {
    instance_id = huaweicloud_compute_instance.basic.id
    volume_id   = huaweicloud_evs_volume.myvol.id
}
resource "huaweicloud_vpc_eip" "myeip" {
    publicip {
        type = "5_sbgp"
    }
    bandwidth {
        name        = "wmm"
        size        = 5
        share_type  = "PER"
        charge_mode = "traffic"
    }
}
resource "huaweicloud_compute_eip_associate" "associated" {
    public_ip   = huaweicloud_vpc_eip.myeip.address
    instance_id = huaweicloud_compute_instance.basic.id
}
